mapper.select:
select s.* FROM spot_check_task s
left join hospitalMonitor m on s.hm_id=m.id
WHERE s.task_state = #{taskState} and m.hisid=#{hisid} and m.dept=#{deptId}

select count(1) from   hospitalmonitor b
left join hisinfo c on b.hisid=c.id left join dictionary d on d.id = b.dept
left join (select max(f.create_time) as createTime ,f.hm_id from spot_check_task f GROUP BY f.hm_id) m on b.id=m.hm_id

    exists(select e.* from spot_check_task e where b.id=e.hm_id)

        and c.name like concat('%',#{hospitalName},'%')


        and b.outstatus =#{outstatus}


        and b.dept = #{dept}


        and b.extractstatus = #{extractstatus}


        and b.indate >= #{inDateBegin}


        and b.indate <= #{inDateEnd}


        and m.createTime >= #{spoCheckDateBegin}


        and m.createTime <= #{spoCheckDateEnd}

select m.createTime,b.id,c.name hospitalName,d.name dictName,b.outstatus,b.extractstatus,b.name,b.sex,b.percode,b.indate from   hospitalmonitor b
left join hisinfo c on b.hisid=c.id left join dictionary d on d.id = b.dept
left join (select max(f.create_time) as createTime,f.hm_id from spot_check_task f GROUP BY f.hm_id) m on b.id=m.hm_id

exists(select e.* from spot_check_task e where b.id=e.hm_id)

        and c.name like concat('%',#{hospitalName},'%')


        and b.outstatus =#{outstatus}


        and b.dept = #{dept}


        and b.extractstatus = #{extractstatus}


        and b.indate >= #{inDateBegin}


        and b.indate <= #{inDateEnd}


        and m.createTime >= #{spoCheckDateBegin}


        and m.createTime <= #{spoCheckDateEnd}


ORDER BY


        ${column} ${order}


        b.extractstatus DESC


LIMIT ${startIndex}, ${pageSize}

select b.id,c.name hospitalName,d.name dictName,b.outstatus,b.extractstatus,b.name,b.sex,b.percode,b.indate from   hospitalmonitor b
left join hisinfo c on b.hisid=c.id left join dictionary d on d.id = b.dept

    exists(select e.* from spot_check_task e where b.id=e.hm_id)

        and c.name like concat('%',#{hospitalName},'%')


        and b.outstatus =#{outstatus}


        and b.dept = #{dept}


        and b.extractstatus = #{extractstatus}
